package util;

import java.time.LocalDate;
import java.util.HashMap;
import java.util.Map;

public class CalendarHelper {
    //todo https://isdayoff.ru/extapi/
    private static Map<Integer, String> workDays = new HashMap<Integer, String>() {{
        put(2019, "");
        put(2020, "");
        put(2021, "");
    }};

    public static boolean isHoliday(LocalDate date) {
        return workDays.get(date.getYear()).charAt(date.getDayOfYear() - 1) == '1';
    }
}
